import React from "react";
import { Link } from "react-router-dom";
import ROUTES from "../../app/routes";
// import selector
import { createSlice } from '@reduxjs/toolkit';

//On the '/topics' page: 

// Users can view all topics * Users can click on an individual 
//topic and be redirected to the page for that topic

export const topicsSlice = createSlice({
  name: 'topics',
  initialState: {
    //This topics object will eventually hold all topics keyed by id
    topics: {

    }
  },
  reducers: {
    addTopic: (state, action) => {
      const { id, name, icon } = action.payload;
      state.topics[id] = {
        id,
        name,
        icon,
        //will correspond to an array containing the ids of each quiz associated with the topic. 
        //When a topic is first created, it won’t have any associated quizzes
        quizIds: []


      }
    }
  }
});
//a selector that selects the topics object nested within initialState
//Export the selector as well as the action creators and reducer that your slice generates.
export const selectTopics = state => state.topics.topics;
export const { addTopic } = topicsSlice.actions;
export const topicsReducer = topicsSlice.reducer;


export default function Topics() {
  const topics = {}; // replace this with a call to your selector to select all the topics in state

  return (
    <section className="center">
      <h1>Topics</h1>
      <ul className="topics-list">
        {Object.values(topics).map((topic) => (
          <li className="topic" key={topic.id}>
            <Link to={ROUTES.topicRoute(topic.id)} className="topic-link">
              <div className="topic-container">
                <img src={topic.icon} alt="" />
                <div className="text-content">
                  <h2>{topic.name}</h2>
                  <p>{topic.quizIds.length} Quizzes</p>
                </div>
              </div>
            </Link>
          </li>
        ))}
      </ul>
      <Link
        to={ROUTES.newTopicRoute()}
        className="button create-new-topic-button"
      >
        Create New Topic
      </Link>
    </section>
  );
}
