import { createSlice } from '@reduxjs/toolkit';

export const topicsSlice = createSlice({
    name: 'topics',
    initialState: {
        //This topics object will eventually hold all topics keyed by id
        topics: {

        }
    },
    reducers: {
        addTopic: (state, action) => {
            const { id, name, icon } = action.payload;
            state.topics[id] = {
                id,
                name,
                icon,
                //will correspond to an array containing the ids of each quiz associated with the topic. 
                //When a topic is first created, it won’t have any associated quizzes
                quizIds: []


            }
        }
    },
    extraReducers: {
        //adds a quiz’s id to the quizIds array of the topic with which the newly created quiz is associated. 
        //This action will receive the same payload the quizzes slice addQuiz
        'quizzes/addQuiz': (state, action) => {
            const { id, name, topicId, cardIds } = action.payload;
            state.topics[topicId].quizIds.push(id);
        }
    }
});
//a selector that selects the topics object nested within initialState
//Export the selector as well as the 
//action creators and 
//reducer that your slice generates.
export const selectTopics = state => state.topics.topics;
export const { addTopic } = topicsSlice.actions;
export const topicsReducer = topicsSlice.reducer;
