import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { v4 as uuidv4 } from "uuid";
import ROUTES from "../app/routes";
// import selectors
import { addCard } from '../features/cards/cardsSlice.js';
import { addQuiz } from '../features/quizzes/quizzesSlice.js';
import { selectTopics } from '../features/topics/TopicsSlice.js';

//On the 'quizzes/new' page:

//Users can create quizzes that are associated with topics and contain lists of flashcards * Users can
// add and remove card fields in the new quiz form

export default function NewQuizForm() {
  const [name, setName] = useState("");
  const [cards, setCards] = useState([]);
  const [topicId, setTopicId] = useState("");
  const navigate = useNavigate();
  const topics = useSelector(selectTopics)//{};  // Replace with topics 
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.length === 0) {
      return;
    }

    const cardIds = [];

    // create the new cards here and add each card's id to cardIds so they can be passed to the action creator which generates new quizzes.
    //iterate through the cards in that form’s local state, and for each one:
    //dispatch your addCard action creator, dispactch to generate an id for each card with uuidv4
    //store created id for each card in cardIds.
    if (cards) {
      cards.forEach(card => {
        const cardId = uuidv4();
        cardIds.push(cardId);

        console.log(`cardId: ${cardId} card: ${card.front} ${card.back}`);
        dispatch(addCard({ front: card.front, back: card.back, id: cardId }));
      })
    }

    // create the new quiz here
    const quizId = uuidv4();

    // dispatch add quiz action
    dispatch(addQuiz({
      id: quizId,
      name: name,
      topicId: topicId,
      cardIds: cardIds
    }));

    navigate(ROUTES.quizzesRoute())
  };

  const addCardInputs = (e) => {
    e.preventDefault();
    setCards(cards.concat({ front: "", back: "" }));
  };

  const removeCard = (e, index) => {
    e.preventDefault();
    setCards(cards.filter((card, i) => index !== i));
  };

  const updateCardState = (index, side, value) => {
    const newCards = cards.slice();
    newCards[index][side] = value;
    setCards(newCards);
  };

  return (
    <section>
      <h1>Create a new quiz</h1>
      <form onSubmit={handleSubmit}>
        <input
          id="quiz-name"
          value={name}
          onChange={(e) => setName(e.currentTarget.value)}
          placeholder="Quiz Title"
        />
        <select
          id="quiz-topic"
          onChange={(e) => setTopicId(e.currentTarget.value)}
          placeholder="Topic"
        >
          <option value="">Topic</option>
          {Object.values(topics).map((topic) => (
            <option key={topic.id} value={topic.id}>
              {topic.name}
            </option>
          ))}
        </select>
        {cards.map((card, index) => (
          <div key={index} className="card-front-back">
            <input
              id={`card-front-${index}`}
              value={cards[index].front}
              onChange={(e) =>
                updateCardState(index, "front", e.currentTarget.value)
              }
              placeholder="Front"
            />

            <input
              id={`card-back-${index}`}
              value={cards[index].back}
              onChange={(e) =>
                updateCardState(index, "back", e.currentTarget.value)
              }
              placeholder="Back"
            />

            <button
              onClick={(e) => removeCard(e, index)}
              className="remove-card-button"
            >
              Remove Card
            </button>
          </div>
        ))}
        <div className="actions-container">
          <button onClick={addCardInputs}>Add a Card</button>
          <button type="submit">Create Quiz</button>
        </div>
      </form>
    </section>
  );
}
