import { createSlice } from '@reduxjs/toolkit';
export const cardsSlice = createSlice({
    name: 'cards',
    initialState: {
        cards: {

        }
    },
    reducer: {
        addCard(state, action) {
            const { id, front, back } = action.payload;
            state.cards[id] = {
                id,
                front,
                back
            }
        }
    }
})

export const selectCards = state => state.cards.cards;
